var React = require('react');
var Layout = require('./layout');


class SingleRecipe extends React.Component {
  render() {

            let recipeInstructions = this.props.ccb.instructions.map(item => {
                              return <li>{item}</li>
                            });

            let recipeIngredients = this.props.ccb.ingredients.map(item => {
                              return <li>{item}</li>
                            });

            let recipeImage = this.props.ccb.img;

            let recipeTitle = this.props.ccb.title;

    return (
                <Layout>
                    <div class="recipe-page">
                      <h1>{recipeTitle}</h1>
                        <div class="row">

                          <div class="col-md-8 recipe-image">
                            <img src={recipeImage}/>
                          </div>

                          <div class="col-md-4 recipe-ingredients">
                            <h2> INGREDIENTS </h2>
                            <ul>
                                {recipeIngredients}
                            </ul>
                          </div>

                        </div>

                        <div class="row recipe">
                            <div class="col-md-12 col-sm-12 recipe-instructions">
                              <h2> Instructions</h2>
                                <ol>
                                    {recipeInstructions}
                                </ol>
                            </div>

                        </div>

                         <div class="row recipe-buttons">
                            <form method="post" action="/test">
                             <div class="col-md-6">
                                <button type="button" class="btn btn-primary btn-lg btn-block">Edit</button>
                             </div>
                            </form>
                                <br/>
                             <div class="col-md-6">
                                <button type="button" class="btn btn-danger btn-lg btn-block" >Delete</button>
                             </div>

                         </div>

                      </div>

                </Layout>
    );
  }
}

module.exports = SingleRecipe;
